import "./post.css";

const Post = ({ post }) => {
    return (
        <div className="post">
            <img className="postImg" src={post.photo} />
            <div className="postInfo">
                <div className="postCats">
                    <span className="postCat">{post.categories}</span>
                </div>
                <span className="postTitle">{post.title}</span>
                <hr />
                <span className="postDate">{new Date(post.date).toLocaleString()}</span>
            </div>
            <p className="postDesc">{post.desc}</p>
        </div>
    );
}

export default Post
